import React, { useState,useEffect } from 'react'
import axios from 'axios';
const API_KEY = process.env.REACT_APP_GIPHY_API_KEY;

const Random = () => {

  const [gif,setGif] = useState('');

  async function fetchData(){
    const url = `https://api.giphy.com/v1/gifs/random?api_key=${API_KEY}`;
    // const output = await axios.get(url);
    // console.log(output);

    const {data} = await axios.get(url);
    const imageSource = data.data.images.downsized_large.url;
    console.log(imageSource);
    setGif(imageSource);
  }

  useEffect(
    ()=>{
      fetchData();
    }
  )
  
  

  function clickHandler(){
      
  }
  return (
    <div className='w-1/2 h-[450px] bg-green-500 rounded-lg border border-black flex flex-col items-center gap-y-5 mt-[15px]'>
      <h1 className='text-2xl underline uppercase font-bold'>Random Gif</h1>
      <img src = {gif}/>
      <button onClick={clickHandler} className='w-10/12 bg-yellow-500 text-lg py-2 rounded-lg'>Generate</button>
    </div>
  )
}

export default Random
